import React, { useState } from 'react';

const SearchBar = () => {
  const baseSearchBar = {
    searchInput: '',
    ingredientRadio: '',
    nameRadio: '',
    firstLetterRadio: '',
  };

  const [searchState, setSearchState] = useState(baseSearchBar);

  return (
    <form>
      <input placeholder="Buscar receita" data-testid="search-input" />
      <label htmlFor="ingredient-radio">
        Ingrediente
        <input
          data-testid="ingredient-search-radio"
          type="radio"
          name="ingredient-radio"
        />
      </label>
      <label htmlFor="name-radio">
        Nome
        <input data-testid="name-search-radio" type="radio" name="name-radio" />
      </label>
      <label htmlFor="first-letter-radio">
        Primeira letra
        <input
          data-testid="first-letter-search-radio"
          type="radio"
          name="first-letter-radio"
        />
      </label>
      <button type="button" data-testid="exec-search-btn" />
    </form>
  );
};

export default SearchBar;
