import React, { useState } from 'react';

const SearchBar = () => {
  const baseSearchBar = {
    searchInput: '',
    ingredientRadio: '',
    nameRadio: '',
    firstLetterRadio: '',
  };

  const [searchState, setSearchState] = useState(baseSearchBar);
  const handleChange = ({ target: value, name }) => {
    const changed = { name: value };
    setSearchState({ ...searchState, name: value });
  };

  return (
    <form>
      <input placeholder="Buscar receita" data-testid="search-input" />
      <label htmlFor="ingredientRadio">
        Ingrediente
        <input
          data-testid="ingredient-search-radio"
          type="radio"
          name="ingredientRadio"
        />
      </label>
      <label htmlFor="nameRadio">
        Nome
        <input data-testid="name-search-radio" type="radio" name="nameRadio" />
      </label>
      <label htmlFor="firstLetterRadio">
        Primeira letra
        <input
          data-testid="first-letter-search-radio"
          type="radio"
          name="firstLetterRadio"
        />
      </label>
      <button type="button" data-testid="exec-search-btn">Buscar</button>
    </form>
  );
};

export default SearchBar;
